package day15;

public class Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] age = {12, 4, 5};

		   // loop through the array
		   // using for loop
		   System.out.println("Using for Loop:");
		   for(int i = 0; i < age.length; i++) {
		     System.out.println(age[i]);
		   }

	}

}
